mapper.select:
SELECT p.user_no, p.music_id
FROM playlist p
         JOIN music m ON p.music_id = m.music_id
WHERE m.category_id = #{searchCategory}
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM playlist p
WHERE p.user_no = #{userNo}

SELECT p.music_id musicId
FROM playlist p
WHERE p.user_no = #{userNo}
